mapper.select:
SELECT feed.* , tm.cstate,tm.cresult from (SELECT 'online:'||f.clanguage||':'||f.country||':'||m.csku cnodeid, m.csku csku,d.ctitle ctitle,d.cdescription cdescription,d.ilanguageid ilanguage, m.iwebsiteid iwebsiteid,f.country ctargetcountry,f.ccurrency ccountrycurrency, f.clanguage clanguage,b.cpath googlecategory,b.cname cname from t_google_category_base b ,t_google_product_category_mapper m , t_google_product_detail d ,t_google_feeds_base f where b.iid=m.icategory and m.csku=d.csku and d.ilanguageid=f.ilanguageid
and m.csku=#{searchDto.csku}
and b.cname ilike '%'||#{searchDto.googlecategory}||'%'
) feed
,t_merchants_product tm where feed.cnodeid=tm.cnodeid and tm.cstate=#{searchDto.cstate}
LEFT JOIN t_merchants_product tm on feed.cnodeid=tm.cnodeid
ORDER BY feed.cnodeid desc limit #{searchDto.pageSize} offset (#{searchDto.page}-1)*#{searchDto.pageSize}

SELECT count(1) from (SELECT 'online:'||f.clanguage||':'||f.country||':'||m.csku cnodeid, m.csku csku,d.ctitle ctitle,d.cdescription cdescription,d.ilanguageid ilanguage, m.iwebsiteid iwebsiteid,f.country ctargetcountry,f.ccurrency ccountrycurrency, f.clanguage clanguage,b.cpath googlecategory,b.cname cname from t_google_category_base b ,t_google_product_category_mapper m , t_google_product_detail d ,t_google_feeds_base f where b.iid=m.icategory and m.csku=d.csku and d.ilanguageid=f.ilanguageid
and m.csku=#{searchDto.csku}
and b.cname ilike '%'||#{searchDto.googlecategory}||'%'
) feed
,t_merchants_product tm where feed.cnodeid=tm.cnodeid and tm.cstate=#{searchDto.cstate}
LEFT JOIN t_merchants_product tm on feed.cnodeid=tm.cnodeid

SELECT tf.cnodeid ,tf.csku,tf.country ctargetcountry,tf.clanguage from 
	(select 'online:'||f.clanguage||':'||f.country||':'||#{csku} cnodeid,f.clanguage,f.country,#{csku} csku from  t_google_feeds_base f
	where 1=1

		and	f.clanguage = #{clanguage}


		and	f.country = #{ccountrycurrency}

) tf 
where 1=1
and not EXISTS(SELECT p.cnodeid from  t_merchants_product	p
				where  p.cnodeid=tf.cnodeid)


SELECT r.cnodeid,r.csku,r.clanguage,r.country ctargetcountry from (
	select 'online:'||f.clanguage||':'||f.country||':'||b.csku cnodeid ,f.clanguage,f.country,b.csku from 
				(select DISTINCT ib.csku from (
							SELECT pb.clistingid ,  pb.csku from t_product_base pb
								where  pb.istatus=1 and pb.iwebsiteid=1 
								ORDER BY pb.clistingid
							limit #{pageSize} offset 
									#{pageSize} * (#{page} - 1)
		     ) ib
	) b,
	(SELECT tf.country,tf.clanguage from t_google_feeds_base tf
		where 1=1

			and tf.clanguage=#{clanguage}


			and tf.country=#{ctargetcountry}

	) f
) r
where 1=1
and not EXISTS(SELECT m.cnodeid from t_merchants_product m 
						where  m.cnodeid=r.cnodeid )

select csku, ctargetcountry, clanguage, cnodeid from t_merchants_product t where 1=1
and t.ctargetcountry=#{ctargetcountry}
and t.clanguage=#{clanguage}
ORDER BY cnodeid limit #{pageSize} offset (#{page}-1)*#{pageSize}

select count(1)
from t_merchants_product t
where 1=1

		and t.ctargetcountry=#{ctargetcountry}


		and t.clanguage=#{clanguage}

SELECT b.clistingid, T .csku, T .ctitle, T .cdescription, T .iwebsiteid, T .ilanguage, b.iqty icount, b.istatus iid, T .cchannel, b.fprice productprice, T .ctargetcountry, T .ccountrycurrency, T .clanguage, T .googlecategory, T .cpath, T .cname, T .cstate, T . CREATEUSER, T .cnodedata, T .cnodeid FROM t_merchants_product T LEFT JOIN t_product_base b ON T .csku = b.csku where b.iwebsiteid = 1
and T.ctargetcountry=#{ctargetcountry}
and T.clanguage=#{clanguage}
ORDER BY T .cnodeid limit #{pageSize} offset (#{page}-1)*#{pageSize}

SELECT count(1) FROM t_merchants_product T LEFT JOIN t_product_base b ON T .csku = b.csku where b.iwebsiteid = 1
and T.ctargetcountry=#{ctargetcountry}
and T.clanguage=#{clanguage}
